templates.template:
pages {
	page ${pageName} {
		file extension "${fileExtension}"
		id ${viewpointName}.${pageName_id}
		label "Page Label"
		index ${pageIndex}
		Overview {
			description "<p><b>title</b></p><p>description</p>"
			image on :  "${OverviewImagePathOn}"
			image off : "${OverviewImagePathOff}"
		}
		header {
			image on : "${PageImagePathOn}"
			image off : "${PageImagePathOff}"
		}
		tab name  "${tabName}"
		show viewer : true
		sections {
			section ${sectionName} {
				description "<p>description</p>"
				id ${viewpointName}.${sectionName_id}
				label "Section Label"
				index ${sectionIndex}
				filtering : true
				expanded : false
				activities {
					activity ${activityName} {
						description "description"
						id ${viewpointName}.${activityName_id}
						label "Activity Label"
						index ${activityIndex}
						predicated : false
						icon : "${iconPath}"
					}
				}
			}
		}
	}
}

